mapper.select:
select s.*,c.name category_name from setmeal s left join category c on s.category_id = c.id

   and s.name like concat('%',#{name},'%')
   and s.category_id=#{categoryId}
   and s.status=#{status}

   order by update_time desc

mapper.update:
update setmeal
name=#{name}, category_id=#{categoryId}, create_time=#{createTime}, update_time=#{updateTime}, price=#{price}, update_user=#{updateUser}, description=#{description}, status=#{status}, image=#{image}
where id =#{id}
